function h = uiProgressBar(varargin)%uiProgressBar: A waitbar that can be embedded in a GUI figure.    if ishandle(varargin{1}) && size(varargin, 2) > 1        ax = varargin{1};        value = varargin{2};        p = get(ax,'Child');        x = get(p,'XData');        x(3:4) = value;        set(p,'XData',x)        set(p,'FaceColor',varargin{3});        return    endend